Index: 
===================================================================
--- 
+++ 
@@ -0,0 +1,230 @@
+@import 'tailwindcss';
+@import '@fontsource/fira-mono';
+@plugin '@tailwindcss/forms';
+@plugin '@tailwindcss/typography';
+
+
+:root {
+  /* Font */
+  --font-body: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Inter, Arial, "Noto Sans", "Helvetica Neue", sans-serif;
+  --font-mono: 'Fira Mono', ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", monospace;
+
+  /* Colori (chiaro) */
+  --color-bg: #ffffff;
+  --color-text: #0a0a0a;
+  --color-muted: #4b5563;        /* grigio testo secondario */
+  --color-border: #e5e7eb;       /* grigio bordi */
+  --color-link: #0a0a0a;         /* link come testo (sottolineati) */
+  --color-code-bg: #f8fafc;      /* sfondo code/pre */
+
+  /* Layout */
+  --column-width: 44rem;         /* ~704px */
+  --container-pad: 6rem;         /* padding verticale contenitore */
+}
+
+html, body {
+  height: 100%;
+}
+
+body {
+  margin: 0;
+  min-height: 100vh;
+  background: var(--color-bg);
+  color: var(--color-text);
+  font-family: var(--font-body);
+  line-height: 1.7;
+  -webkit-font-smoothing: antialiased;
+  -moz-osx-font-smoothing: grayscale;
+  text-rendering: optimizeLegibility;
+}
+
+/* Colonna centrale stretta */
+.text-column {
+  display: block;
+  max-width: var(--column-width);
+  margin-inline: auto;
+  padding: var(--container-pad) 1.2rem;
+}
+
+/* Tipografia base */
+h1, h2, h3 {
+  font-weight: 700;
+  letter-spacing: -0.02em;
+  margin: 0 0 0.75rem 0;
+}
+
+h1 {
+  /* grande ma non urlato */
+  font-size: clamp(2rem, 3.2vw + 1rem, 3.25rem);
+  line-height: 1.15;
+  text-align: left; /* niente centrato */
+}
+
+h2 {
+  font-size: 1.125rem; /* ~18px */
+  line-height: 1.35;
+  color: var(--color-text);
+  margin-top: 2.25rem;
+}
+
+p, ul, ol, pre, blockquote, figure {
+  margin: 0 0 1rem 0;
+}
+
+p + p {
+  margin-top: 0.5rem;
+}
+
+small, .muted {
+  color: var(--color-muted);
+}
+
+/* Link: stessi del testo, solo sottolinea */
+a {
+  color: var(--color-link);
+  text-decoration: underline;
+  text-underline-offset: 3px;
+  text-decoration-thickness: from-font;
+  text-decoration-color: rgba(0,0,0,0.25);
+}
+a:hover {
+  text-decoration-color: currentColor;
+}
+
+/* Liste pulite */
+ul, ol {
+  padding-left: 1.25rem;
+}
+li + li { margin-top: 0.25rem; }
+
+/* Separatore sottile */
+hr {
+  border: 0;
+  border-top: 1px solid var(--color-border);
+  margin: 2.5rem 0;
+}
+
+/* Code & Pre */
+code {
+  font-family: var(--font-mono);
+  font-size: 0.95em;
+  background: var(--color-code-bg);
+  padding: .15em .35em;
+  border-radius: 4px;
+}
+pre {
+  font-size: 0.95rem;
+  font-family: var(--font-mono);
+  background: var(--color-code-bg);
+  border: 1px solid var(--color-border);
+  border-radius: 8px;
+  padding: 1rem 1rem;
+  overflow-x: auto;
+  box-shadow: none;
+  color: inherit;
+}
+
+/* Immagini e figure */
+img, svg, video {
+  display: block;
+  max-width: 100%;
+  height: auto;
+}
+figure > img { border-radius: 8px; }
+
+/* Moduli (plugin forms fa già il grosso) */
+input, textarea, select, button {
+  font: inherit;
+  color: inherit;
+}
+button, .btn {
+  display: inline-flex;
+  align-items: center;
+  gap: .5rem;
+  padding: .5rem .8rem;
+  border: 1px solid var(--color-border);
+  background: transparent;
+  border-radius: 8px;
+  cursor: pointer;
+}
+button:hover, .btn:hover {
+  background: #f9fafb;
+}
+button:focus-visible, .btn:focus-visible, a:focus-visible {
+  outline: 2px solid #111;
+  outline-offset: 3px;
+}
+
+/* Capsule per skill/tag, utili per la sezione "Skills" */
+.tag {
+  display: inline-block;
+  border: 1px solid var(--color-border);
+  border-radius: 999px;
+  padding: .25rem .6rem;
+  font-size: .8125rem;
+  line-height: 1;
+  margin: .25rem .25rem 0 0;
+}
+
+/* Typography plugin: imposta prose più “sobria” */
+.prose {
+  max-width: none; /* lascia larghi i paragrafi, gestisce .text-column */
+}
+.prose :where(h1, h2, h3, h4) {
+  font-weight: 700;
+  letter-spacing: -0.02em;
+}
+.prose :where(a) {
+  color: var(--color-link);
+  text-decoration: underline;
+  text-underline-offset: 3px;
+  text-decoration-color: rgba(0,0,0,0.25);
+}
+.prose :where(blockquote) {
+  border-left: 3px solid var(--color-border);
+  padding-left: 1rem;
+  color: var(--color-muted);
+}
+.prose :where(code) {
+  background: var(--color-code-bg);
+  padding: .15em .35em;
+  border-radius: 4px;
+}
+
+/* Layout verticale più arioso tra le sezioni principali */
+main > * + * {
+  margin-top: 2rem;
+}
+
+/* Accessibilità */
+.visually-hidden {
+  border: 0;
+  clip: rect(0 0 0 0);
+  height: auto;
+  margin: 0;
+  overflow: hidden;
+  padding: 0;
+  position: absolute;
+  width: 1px;
+  white-space: nowrap;
+}
+
+/* Responsive: leggermente più grande su schermi medi */
+@media (min-width: 720px) {
+  h1 { font-size: clamp(2.25rem, 2.4vw + 1.25rem, 3.5rem); }
+  .text-column { padding-block: 7rem; }
+}
+
+
+/* @media (prefers-color-scheme: dark) {
+  :root {
+    --color-bg: #0b0b0b;
+    --color-text: #e5e7eb;
+    --color-muted: #9ca3af;
+    --color-border: #1f2937;
+    --color-link: #e5e7eb;
+    --color-code-bg: #0f172a;
+  }
+  button:hover, .btn:hover { background: #0f1115; }
+  a { text-decoration-color: rgba(255,255,255,0.35); }
+} */

